mapper.select:
SELECT * FROM task
WHERE course_id = #{courseId}
ORDER BY created_at DESC
    LIMIT #{size} OFFSET #{offset}

mapper.update:
UPDATE task
title = #{title}, description = #{description}, type = #{type, typeHandler=com.sx.backend.typehandler.TaskTypeTypeHandler}, deadline = #{deadline}, max_score = #{maxScore}, updated_at = NOW()
WHERE task_id = #{taskId}
